           event.preventDefault();
           var eventId = event.target.id;
           setTimeout(function() {
               if(eventId == 'sidebarNavLogButton')
                   AMP.setState({ stateSidebarNav: { userOptions: false } })
               else
                   AMP.setState({ stateHdr: { userOptions: false } })
           }, 250);
