function mobileScreen() {
                            return window.innerWidth < 763;
                        }
                        var lastScrollTop = 0;
                        window.addEventListener("load", function(){
                            window.addEventListener("scroll", (event) => {
                                var top = window.pageYOffset || document.documentElement.scrollTop;
                                if (top > lastScrollTop && top > 45 && mobileScreen()) {
                                  AMP.setState({ stateHdr: { search: false, searchDesk: false } })
                                } else if (top < lastScrollTop && mobileScreen()) {
                                  AMP.setState({ stateHdr: { search: true, searchDesk: true } })
                                }
                                lastScrollTop = top <= 0 ? 0 : top;
                            },false);
                            window.addEventListener("resize", (event) => {
                                if (!mobileScreen())
                                    AMP.setState({ stateHdr: { search: true, searchDesk: true } });
                            });
                        });

