padding: 4px;
}


/*===========================
Heading
============================*/
.Cambridge_SR_heading {

}

.Cambridge_SR_heading_link {
color: #1d2a57; /* navy */
font-family: Arial, Helvetica, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
font-size: 18px;
font-weight: 400;
line-height: 1.4;
text-align: left;
text-decoration: none;
display: -webkit-box;
-webkit-box-orient: vertical;
overflow: hidden;
-webkit-line-clamp: 2;
}

/* When displayed in fixed right rail slot	 */
#ad_rightslot .tlod .Cambridge_SR_heading_link {
font-size: 15px;
-webkit-line-clamp: 1;
line-height: 1.1
}

.Cambridge_SR_heading_link:hover {
color: #1a3561; /* blue */
text-decoration: none;
}

/*===========================
Sponsor/Advertiser
============================*/
.Cambridge_SR_sponsor {
margin-top: 4px;
}

#ad_rightslot .tlod .Cambridge_SR_sponsor {
margin-top: 2px;
}

.Cambridge_SR_sponsor_link {
color: #1d2a57; /* navy */
font-family: Arial, Helvetica, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
font-size: 13px;
font-weight: 600;
line-height: 1.3;
text-decoration: none;
display: -webkit-box;
-webkit-box-orient: vertical;
overflow: hidden;
-webkit-line-clamp: 1;
}

#ad_rightslot .tlod .Cambridge_SR_sponsor_link {
font-size: 11px;
line-height: 1;
}

.Cambridge_SR_sponsor:hover {
color: #0580e8 !important; /* light blue */
}


/*===========================
Caption
============================*/
.Cambridge_SR_caption {
margin-top: 6px;

}

.Cambridge_SR_caption_link {
color: #333;
font-family: "Source Sans Pro", sans-serif;
font-size: 14px;
font-weight: 400;
line-height: 1.4;
text-decoration: none;
display: -webkit-box;
-webkit-box-orient: vertical;
overflow: hidden;
-webkit-line-clamp: 4;
}

/* When displayed in fixed right rail slot	 */
#ad_rightslot .tlod .Cambridge_SR_caption_link {
display: none;
}

.Cambridge_SR_caption_link:hover {
text-decoration: none;
}


/*===========================
Call to Action
============================*/
.Cambridge_SR_textcontent_cta {
margin-top: 10px;
text-align: right;
}

/* When displayed in fixed right rail slot	 */
#ad_rightslot .tlod .Cambridge_SR_textcontent_cta {
margin-top: 0;
}

.Cambridge_SR_cta {
display: inline-block;
padding: 6px 18px;
border: none;
color: #fff !important;
background-color: #1d2a57; /* navy */
font-family: Arial, Helvetica, sans-serif;
font-size: 15px;
font-weight: bold;
line-height: 16px;
letter-spacing: normal;
text-align: center;
text-decoration: none;
border-bottom: dashed 0 rgba(0,0,0,.6) !important;
border-radius: 25px;
}

/* When displayed in fixed right rail slot	 */
#ad_rightslot .tlod .Cambridge_SR_cta {
font-size: 13px;
padding: 4px 12px;
}

.Cambridge_SR_cta:hover {
color: #fff !important;
background-color: #004aac; /* blue */
text-decoration: none !important;
}


