.hfl-m .tlod .Cambridge_SR_heading_link {
font-size: 15px;
}

.hfl-m .tlod .Cambridge_SR_sponsor_link {
font-size: 12px;
}

.hfl-m .tlod .Cambridge_SR_cta {
width: 100%;
}
