var adIndexes = {
    mpuslot: 1, marketingslot: 1, ihp_houseslot: 1, ihp_ringlinkslot: 1
}

var classesToSkip = [];

function loadNextPage(eligibleIds) {
    var userHistory = JSON.parse(localStorage.getItem("recommendations"));
    if (userHistory && Object.keys(userHistory).length > 0)
        userHistory = mergeHistory(userHistory, eligibleIds);
    else
        userHistory = eligibleIds;

    localStorage.setItem("recommendations", JSON.stringify(userHistory));
    isLoading = true;

    var next_tiles = '/next_tiles';
    var formData = new FormData();
    formData.append("eligibleIds", JSON.stringify(eligibleIds));
    formData.append("adIndexes", JSON.stringify(adIndexes));
    fetch(next_tiles, {
        method: 'POST',
        body: formData
    }).then(res => {
        return res.text();
    }).then(data => {
        var container = document.getElementById("items");
        let li = document.createElement("li");
        li.innerHTML = data;
        container.appendChild(li);
        Waypoint.refreshAll();

        if (typeof iaw !== 'undefined' && typeof iaw.loadNewSlot === 'function') {
            for(const slot in adIndexes){
                if(loadNewSlot(slot)){
                    break;
                }
            }
        }
        isLoading = false;
    });
}

function loadNewSlot(slot){
    let slotId = 'ad_' + slot +'_' + adIndexes[slot];
    if(document.getElementById(slotId)){
        try {
            if(slot === "marketingslot"){
                // Retrieve the value of the cookie
                let loggedIn = Cookies.get('hasloggedin');
                iaw.loadNewSlot(slotId, { 'si': adIndexes[slot], 'u_logstatus': loggedIn });
            } else {
                iaw.loadNewSlot(slotId, { 'si': adIndexes[slot] });
            }
        } catch (e) {
            console.log("Error loading iaw slot for SLOT ID : " + slotId, e);
        }
        adIndexes[slot]++;
        return true;
    }
    return false;
}

function mergeHistory(json1, json2) {
    const merged = {};
    for (const className in json2) {
        if (json1.hasOwnProperty(className)) {
            merged[className] = json1[className]
            ? Array.isArray(json1[className])
                ? [...json1[className]]
                : [json1[className]]
            : [];
            if (json2[className]) {
                if (Array.isArray(json2[className])) {
                    // If both are arrays, merge while overriding dates
                    for (const item2 of json2[className]) {
                        const existingMatchingIndex = merged[className].findIndex(
                          (item1) => item1.id === item2.id
                        );
                        if (existingMatchingIndex !== -1) {
                            merged[className][existingMatchingIndex].date = item2.date;
                        } else {
                            merged[className].push(item2);
                        }
                    }
                } else {
                    // If json2[className] is not an array, find a matching entry and update
                    const matchingEntryIndex = merged[className].findIndex(
                        (item) => item.id === json2[className].id
                    );
                    if (matchingEntryIndex !== -1) {
                        merged[className][matchingEntryIndex].date = json2[className].date;
                    } else {
                        // If no match, add as a new entry
                        merged[className].push([json2[className]]);
                    }
                }
            }
        } else {
            merged[className] = json2[className];
        }
    }
    return merged;
}

function loadRecommendations() {
    let userHistory = localStorage.getItem("recommendations") || "{}";
    var formData = new FormData();
    if (userHistory)
        formData.append("userHistory", userHistory);
    return fetch("/load_recommendations", {
        method: 'POST',
        body: formData
    }).then((res) => {
        if (res.ok)
            return res.json()
        throw new Error('Something went wrong');
    });
}

function reloadRecommendations(emptyClasses) {
    let userHistory = localStorage.getItem("recommendations") || "{}";
    let formData = new FormData();
    if (userHistory)
        formData.append("userHistory", userHistory);
    if (emptyClasses)
        formData.append("emptyClasses", emptyClasses);
    return fetch("/reload_recommendations", {
        method: 'POST',
        body: formData
    });
}

function clearUserHistory(productClass) {
    const data = JSON.parse(localStorage.getItem("recommendations"));
    for (const className in data) {
        if(productClass === className)
            delete data[className];
    }
    localStorage.setItem("recommendations", JSON.stringify(data));
}

function temporalClearUserHistory() {
    const data = JSON.parse(localStorage.getItem("recommendations"));
    if (data) {
        const date = new Date().getTime();
        const threshold = 24 * 60 * 60 * 1000;
        for (const className in data) {
            const objects = data[className];
            for (let i = objects.length - 1; i >= 0; i--) {
              const object = objects[i];
              const objectDate = new Date(object.date).getTime();
              if ((date - objectDate) > threshold) {
                objects.splice(i, 1);
              }
            }
            data[className] = objects;
        }
    }
    localStorage.setItem("recommendations", JSON.stringify(data));
}

function loadTiles(recommendations) {
    var eligibleIds = {};
    var emptyClasses = [];

    for (var productClass of productClasses) {
        if (recommendations[productClass] && recommendations[productClass].recommendationsList[0]) {
            recommendation = recommendations[productClass].recommendationsList[0];
            eligibleIds[productClass] = [{type: recommendation.type, id: recommendation.id, date: new Date()}];
            recommendations[productClass].recommendationsList = recommendations[productClass].recommendationsList.filter(item => item.id != recommendation.id); // delete consumed reco
        }
        else if (!classesToSkip.includes(productClass)) {
            if (recommendations[productClass])
                emptyClasses.push({productClass: productClass, page: recommendations[productClass].page, userSegmentKey: recommendations[productClass].userSegmentKey });
            else
                emptyClasses.push({productClass: productClass, page: 1});
        }
    }
    if (emptyClasses.length > 0) {
        reloadRecommendations(JSON.stringify(emptyClasses)).then((res) => {
            return res.json();
        }).then((newRecommendations) => {
            newRecommendations.forEach((element) => {
                const { recommendationsList, productClass, page, toReset, userSegmentKey } = element;
                if (element.recommendationsList && element.recommendationsList.length == 0) {
                    return;
                }
                if (toReset) {;
                    if (!loopableClasses.includes(productClass)) {
                        if (!classesToSkip.includes(productClass))
                            classesToSkip.push(productClass);
                        return;
                    }
                    clearUserHistory(productClass);
                }
                recommendations[productClass] = {recommendationsList, page: page, userSegmentKey: userSegmentKey};
                recommendation = recommendations[productClass].recommendationsList[0];
                eligibleIds[productClass] = [{type: recommendation.type, id: recommendation.id, date: new Date()}];
                recommendations[productClass].recommendationsList = recommendations[productClass].recommendationsList.filter(item => item.id != recommendation.id); // delete consumed reco
            });
            loadNextPage(eligibleIds);
        });
    }
    else {
        loadNextPage(eligibleIds);
    }
}

var isLoading = false;
var recommendations = {};
window.page = 0;
temporalClearUserHistory();

function startIHP() {
    loadRecommendations().then((reco) => {
        reco.forEach((element) => {
            const { recommendationsList, productClass, page, toReset, userSegmentKey } = element;
            if (element.recommendationsList && element.recommendationsList.length == 0) {
                return;
            }
            if (toReset) {
                clearUserHistory(productClass);
            }
            recommendations[productClass] = {recommendationsList, page: page, userSegmentKey: userSegmentKey};
        });
        loadTiles(recommendations);

        // hide loader
        document.getElementById('loader').style.display='none';

        var waypoint = new Waypoint({
            element: document.getElementById('scope'),
            handler: function(direction) {
                if (direction === 'down' && !isLoading) {
                    loadTiles(recommendations);
                }
            },
            offset: function() {
                var items = document.getElementById('items');
                if (items && items.lastElementChild) {
                    var children = items.lastElementChild.getElementsByClassName('tile');
                    if (children.length > 0) {
                        var screenSizeToTrigger = -(items.clientHeight - 1.8 * innerHeight - children[0].clientHeight);
                        return screenSizeToTrigger;
                    }
                }
                return 'bottom-in-view';
            }
        });
    }).catch((error) => {
       console.log(error);
        document.querySelectorAll(".old-HP").forEach(function(element) {
            element.classList.add("error-IHP");
        });
        document.querySelector("#scope.mobile-content").style.display = "none"
    });

    document.addEventListener('DOMContentLoaded', function() {
        var closeButton = document.getElementById("closeMyModal");
        if(closeButton) {
            closeButton.addEventListener("click", closeModal); // used in case modal opened on refresh
        }
        var tileContainer = document.getElementById('tile-container');
        tileContainer.addEventListener('click', function(event) {
            var target = event.target;
            var tile = target.closest('.tile');
            if (tile) {
                var tileType = tile.getAttribute('data-tile-type') || '';
                if (target.classList.contains('share-button')) {
                    openShareMenu(target);
                    event.stopPropagation(); // Prevents the tap:lightbox_modal event from executing and producing an error
                } else if (tileType.toLowerCase() !== 'wordscramble' && !target.classList.contains('wordlist-add-button') && !target.classList.contains('social-button') && !target.classList.contains('wordlist-button')) {
                    openModal(tile.getAttribute('data-entry-url'));
                } else if (tileType.toLowerCase() === 'wordscramble' && target.parentElement.id == "ws_start_screen_play") {
                    gtag('event','page_view', {
                        "category":  "games" ,
                        "dataset":  "word-scramble" ,
                        "locale": "br"
                    });
                }
            }
        });

        window.addEventListener('popstate', function(event) {
            closeModal();
        });
    });
}

function whenIHPVisible() {
  new IntersectionObserver((entries, observer) => {
    entries.forEach(entry => {
      if(entry.intersectionRatio > 0) {
        startIHP();
        observer.disconnect();
      }
    });
  }).observe(document.querySelector("#tile-container"));
}

whenIHPVisible();

function openShareMenu(button) {
    var shareMenu = button.nextElementSibling;
    var allShareMenus = document.querySelectorAll('.share-menu');
    allShareMenus.forEach(function(menu) {
        if (menu !== shareMenu) {
            menu.classList.remove('active');
        }
    });
    shareMenu.classList.toggle('active');
}

function openModal(url) {
    // Clone the modal template
    var modalTemplate = document.getElementById("modal-template");
    var modalContent = modalTemplate.content.cloneNode(true);

    // Load content into the iframe
    var modalIframe = modalContent.querySelector("#modalIframe");
    modalIframe.src = url;

    // Add the modal to the document
    document.getElementById("modal-box").appendChild(modalContent);

    // Display the modal
    document.getElementById("myModal").style.display = "block";

    // Display stickyslot
    if (typeof iaw !== 'undefined') {
        iaw.enableStickySlot();
        iaw.loadNewSlot("ad_stickyslot");
    }

    // Add an event receiver for the close button when the AMP close action has been performed
    var closeButton = document.getElementById("closeMyModal");
    closeButton.addEventListener("click", closeModal);
}

function closeModal() {
    var modal = document.getElementById("lightbox_modal");
    if (modal) { 
        releaseModalAdSlot();
        modal.remove();
        document.getElementById("tile-container").focus();
    }
}

function releaseModalAdSlot() {
    if (typeof iaw !== 'undefined') {
        iaw.releaseNewSlot("ad_stickyslot");
    }
}


















