              typeSelector: './@data-tile-type'
          },
          unit: {
              idSelector: './@data-tile-id',
              titleSelector: ".//div[contains(@class,'tile-content')]/span[contains(@class,'bold-font')][1]/text()",
              urlSelector: './@data-entry-url'
          },
          ping: {
            minimumVisitLength: 5,
            heartbeatDelay: 20,
          },
          contentTileClick: [
            {
              selector: '.i-facebook .social-button',
              arguments: {
                action: "share",
                details: 'facebook'
              }
            },
            {
              selector: '.i-x .social-button',
              arguments: {
                action: "share",
                details: 'x'
              }
            },
            {
              selector: '.audio-play',
              arguments: {
                action: 'audio-play'
              }
            },
            {
              selector: 'XPATH:self::*',
              arguments: {action: 'view'}
            }
          ]
        }

























